import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import "./App.css";
import No from './pics/No.jpeg'; // Replace with your "No" image path
import Bouquet from './pics/flower.svg'; // Replace with your bouquet image path (or SVG)

const App = () => {
  const [showProposal, setShowProposal] = useState(false);
  const [response, setResponse] = useState(null);
  const [moveNoButton, setMoveNoButton] = useState(false);
  const [buttonPosition, setButtonPosition] = useState({ top: "0px", left: "0px" });
  const [noClicks, setNoClicks] = useState(0); // Track the number of "No" clicks
  const [showBouquet, setShowBouquet] = useState(false); // State for showing bouquet

  // Animation for the bouquet using react-spring
  const bouquetStyle = useSpring({
    opacity: showBouquet ? 1 : 0,
    transform: showBouquet ? "scale(1) translateY(0)" : "scale(0.5) translateY(-200px)",
    config: { tension: 200, friction: 12 },
  });

  const handleProposalClick = () => {
    setShowProposal(true);
  };

  const handleYesClick = () => {
    setResponse("YES");
    setShowBouquet(true); // Show the bouquet when "YES" is clicked
  };

  const handleNoClick = () => {
    // If "No" button is clicked more than 2 times, show the popup modal
    if (noClicks >= 2) {
      setResponse("TOO MANY NOs");
      return;
    }

    // Track the "No" clicks
    setNoClicks(noClicks + 1);

    // When "No" is clicked, move the button
    if (!moveNoButton) {
      setMoveNoButton(true);
      setTimeout(() => {
        setMoveNoButton(false);
      }, 1000);
      setButtonPosition({
        top: `${Math.random() * 200}px`,
        left: `${Math.random() * 200}px`,
      });
    } else {
      setResponse("NO");
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>💌 Be My Valentine 💌</h1>
        <div className="intro">
          <p>
            Hey you! Yes, you! The one with the beautiful smile and the
            incredible personality 😘
          </p>
          <p>Well, I have something important to ask... 🎁</p>
          <button className="proposal-btn" onClick={handleProposalClick}>
            Ask Away! 🥺
          </button>
        </div>

        {showProposal && (
          <div className="proposal">
            <h2 className="proposal-text">Will you be my Valentine? ❤️</h2>
            <div className="response-buttons">
              <button onClick={handleYesClick}>YES, of course! 💖</button>
              <button
                onClick={handleNoClick}
                style={{
                  position: "relative",
                  top: buttonPosition.top,
                  left: buttonPosition.left,
                  transition: "all 0.5s ease-in-out",
                }}
                className={moveNoButton ? "no-button-moving" : ""}
              >
                No, sorry 😭
              </button>
            </div>
          </div>
        )}

        {response && response === "YES" && (
          <div className="response">
            <h3 className="happy-response">YAY! 🎉 We are Valentine's Day official!</h3>
            {/* Animated bouquet */}
            {showBouquet && (
              <animated.div style={bouquetStyle}>
                <img src={Bouquet} alt="Bouquet" className="bouquet-image" />
              </animated.div>
            )}
          </div>
        )}

        {response && response === "NO" && (
          <div className="response">
            <h3 className="sad-response">Oh no... Maybe next year! 😢</h3>
          </div>
        )}

        {response && response === "TOO MANY NOs" && (
          <div className="response">
            <h3 className="angry-response">
              No na no ka ah, hindi mo na ako mahal 😤
            </h3>
            <img src={No} alt="No Image" className="heart-image" />
          </div>
        )}
      </header>
    </div>
  );
};

export default App;
