import React, { useState } from "react";
import Confetti from "react-confetti";  // Import Confetti
import "./App.css";

const App = () => {
  const [showProposal, setShowProposal] = useState(false);
  const [response, setResponse] = useState(null);
  const [moveNoButton, setMoveNoButton] = useState(false);
  const [buttonPosition, setButtonPosition] = useState({ top: "0px", left: "0px" });

  const handleProposalClick = () => {
    setShowProposal(true);
  };

  const handleYesClick = () => {
    setResponse("YES");
  };

  const handleNoClick = () => {
    // When "No" is clicked, move the button
    if (!moveNoButton) {
      setMoveNoButton(true);
      setTimeout(() => {
        setMoveNoButton(false);
      }, 1000); 
      setButtonPosition({
        top: `${Math.random() * 200}px`,
        left: `${Math.random() * 200}px`
      });
    } else {
      setResponse("NO");
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>üíå Be My Valentine üíå</h1>
        <div className="intro">
          <p>
            Hey you! Yes, you! The one with the beautiful smile and the
            incredible personality üòò
          </p>
          <p>Well, I have something important to ask... üéÅ</p>
          <button className="proposal-btn" onClick={handleProposalClick}>
            Ask Away! ü•∫
          </button>
        </div>

        {showProposal && (
          <div className="proposal">
            <h2 className="proposal-text">
              Will you be my Valentine? ‚ù§Ô∏è
            </h2>
            <div className="response-buttons">
              <button onClick={handleYesClick}>YES, of course! üíñ</button>
              <button
                onClick={handleNoClick}
                style={{
                  position: "relative",
                  top: buttonPosition.top,
                  left: buttonPosition.left,
                  transition: "all 0.5s ease-in-out"
                }}
                className={moveNoButton ? "no-button-moving" : ""}
              >
                No, sorry üò≠
              </button>
            </div>
          </div>
        )}

        {response && (
          <div className="response">
            {response === "YES" ? (
              <>
                <h3 className="happy-response">YAY! üéâ We are Valentine's Day official!</h3>
                <Confetti />  {/* Display Confetti when YES is clicked */}
              </>
            ) : (
              <h3 className="sad-response">Oh no... Maybe next year! üò¢</h3>
            )}
          </div>
        )}
      </header>
    </div>
  );
};

export default App;
