import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import ReactConfetti from "react-confetti";
import "./App.css";
import Envelope from './pics/Envelope.svg'; 
import No from './pics/No.jpeg'; 
import Flowers from './pics/FlowersForYou.gif'; 
import GogiImage from './pics/Gogi.jpg'; 
import CAT from './pics/CAT.gif'; 
import No3 from './pics/No3.gif'; 
import No4 from './pics/No4.gif';

const App = () => {
  const [showEnvelope, setShowEnvelope] = useState(true);
  const [showProposal, setShowProposal] = useState(false);
  const [response, setResponse] = useState(null);
  const [moveNoButton, setMoveNoButton] = useState(false);
  const [buttonPosition, setButtonPosition] = useState({ top: "0px", left: "0px" });
  const [noClicks, setNoClicks] = useState(0); 
  const [showBouquet, setShowBouquet] = useState(false); 
  const [showNextButton, setShowNextButton] = useState(false); 
  const [showPopUp, setShowPopUp] = useState(false); 
  const [showNoPopUp, setShowNoPopUp] = useState(false); 
  const [proceedToNextScreen, setProceedToNextScreen] = useState(false); 
  const [showGogiPopUp, setShowGogiPopUp] = useState(false); 
  const [showModal, setShowModal] = useState(false); 
  const [showNo3PopUp, setShowNo3PopUp] = useState(false); 
  const [showNo4PopUp, setShowNo4PopUp] = useState(false); 

  const [buttonDisabled, setButtonDisabled] = useState(false); 
  const [errorMessage, setErrorMessage] = useState(""); 

  const envelopeAnimation = useSpring({
    opacity: showEnvelope ? 1 : 0,
    transform: showEnvelope ? "scale(1) rotate(0deg)" : "scale(0.8) rotate(180deg)",
    config: { tension: 200, friction: 12 },
  });

  const bouquetStyle = useSpring({
    opacity: showBouquet ? 1 : 0,
    transform: showBouquet ? "scale(1) translateY(0)" : "scale(0.5) translateY(-200px)",
    config: { tension: 200, friction: 12 },
  });

  const handleEnvelopeClick = () => {
    setShowEnvelope(false);
    setTimeout(() => {
      setShowModal(true); 
    }, 1000); 
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setShowProposal(true);
  };

  const handleYesClick = () => {
    setResponse("YES");
    setShowBouquet(true); 
    setTimeout(() => {
      setProceedToNextScreen(true); 
      setShowNextButton(true); // This will show the "Next" button after bouquet animation
    }, 2000); 
  };

  const handleNoClick = () => {
    setNoClicks(noClicks + 1);

    if (noClicks === 0) {
      setResponse("NO");
      setShowNoPopUp(true); 
    } else if (noClicks === 1) {
      setResponse("TOO MANY NOs");
      setShowNoPopUp(false);
      setShowPopUp(true); 
    } else if (noClicks === 2) {
      setResponse("NO3");
      setShowPopUp(false);
      setShowNoPopUp(false);
      setShowNo3PopUp(true);
    } else if (noClicks === 3) {
      setResponse("NO4");
      setShowPopUp(false);
      setShowNoPopUp(false);
      setShowNo3PopUp(false);
      setShowNo4PopUp(true);
    } else if (noClicks === 4) {
      setButtonDisabled(true);
      setErrorMessage("BAWAL NA MAG NO SAPILITAN NA 'TO");
    }

    if (!moveNoButton) {
      setMoveNoButton(true);
      setTimeout(() => {
        setMoveNoButton(false);
      }, 1000);
      setButtonPosition({
        top: `${Math.random() * 200}px`,
        left: `${Math.random() * 200}px`,
      });
    }
  };

  const handleNextButtonClick = () => {
    setShowNextButton(true); 
    setShowGogiPopUp(true); 
  };

  const handleClosePopUp = () => {
    setShowPopUp(false);
    setShowNoPopUp(false);
    setShowNo3PopUp(false);
    setShowNo4PopUp(false);
    setShowGogiPopUp(false);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>💌 Hi, Mahal 💌</h1>

        {showEnvelope && (
          <animated.div style={envelopeAnimation} className="envelope-container" onClick={handleEnvelopeClick}>
            <img src={Envelope} alt="Envelope" className="envelope-image" />
            <p>Open it, Mahal</p>
          </animated.div>
        )}

        {showModal && (
          <div className="modal-monthsary">
            <div className="modal-message">
              <p>Happy monthsary! Thank you for all the time you spend with me...</p>
              <button className="close-btn" onClick={handleCloseModal}>Close</button>
            </div>
          </div>
        )}

        {showProposal && !proceedToNextScreen && (
          <div className="proposal">
            <h2 className="proposal-text">Will you be my Valentine? ❤️</h2>
            <div className="response-buttons">
              <button onClick={handleYesClick}>YES, of course! 💖</button>
              <button
                onClick={handleNoClick}
                style={{
                  position: "relative",
                  top: buttonPosition.top,
                  left: buttonPosition.left,
                  transition: "all 0.5s ease-in-out",
                }}
                className={moveNoButton ? "no-button-moving" : ""}
                disabled={buttonDisabled}
              >
                No, sorry 😭
              </button>
            </div>
          </div>
        )}

        {errorMessage && (
          <div className="error-message">
            <h3>{errorMessage}</h3>
          </div>
        )}

        {showPopUp && (
          <div className="pop-up">
            <div className="pop-up-container">
              <img src={CAT} alt="CAT GIF" className="pop-up-image" />
              <p className="pop-up-message">Inulit pa talaga</p>
              <button className="close-btn" onClick={handleClosePopUp}>Close</button>
            </div>
          </div>
        )}

        {showBouquet && (
          <animated.div style={bouquetStyle} className="bouquet-container">
            <img src={Flowers} alt="Bouquet" className="bouquet-image" />
            <p className="bouquet-message">For you, Mahal! 💐</p>
          </animated.div>
        )}

        {showNextButton && (
          <button className="next-button" onClick={handleNextButtonClick}>And we are going to...</button>
        )}

        {showGogiPopUp && (
          <div className="gogi-pop-up">
            <div className="gogi-pop-up-container">
              <img src={GogiImage} alt="Gogi" className="gogi-pop-up-image" />
              <p className="gogi-pop-up-message">Let's go, Mahal!</p>
              <button className="close-btn" onClick={handleClosePopUp}>Close</button>
            </div>
          </div>
        )}

        {showNo3PopUp && (
          <div className="pop-up">
            <div className="pop-up-container">
              <img src={No3} alt="No3 GIF" className="pop-up-image" />
              <h3 className="no3-response">NAG NO PA TALAGA ULIT </h3>
              <button className="close-btn" onClick={handleClosePopUp}>Close</button>
            </div>
          </div>
        )}

        {showNo4PopUp && (
          <div className="pop-up">
            <div className="pop-up-container">
              <img src={No4} alt="No4 GIF" className="pop-up-image" />
              <h3 className="no4-response">OKAY FINE</h3>
              <button className="close-btn" onClick={handleClosePopUp}>Close</button>
            </div>
          </div>
        )}

      </header>
    </div>
  );
};

export default App;
